/**
 * Return an ETag in the form of `"<size>-<mtime>"`
 * from the given `stat`.
 *
 * @param {Object} stat
 * @return {String}
 * @api private
 */exports.etag=function(e){return'"'+e.size+"-"+Number(e.mtime)+'"'};exports.decode=function(e){try{return decodeURIComponent(e)}catch(t){return-1}};exports.escape=function(e){return String(e).replace(/&(?!\w+;)/g,"&amp;").replace(/</g,"&lt;").replace(/>/g,"&gt;").replace(/"/g,"&quot;")};